from mycode import Crawler
import pytest
from urllib.parse import urlparse
import bson

@pytest.mark.parametrize("input1, input2",
                         [
                             ("http://dbpedia.org/resource/Nanyue_Huisi", "rdf"),
                             ("https://ephesusbreeze.com/es/tours", "jsonld")
                         ]
                         )
def test_are_links_a_list(input1, input2):
  crawler = Crawler()
  links = crawler.getLinks(input1, input2)
  domain = urlparse(input1).netloc
  assert isinstance(links, (list, tuple))
  assert urlparse(links[0]).netloc == domain


@pytest.mark.parametrize("input1, input2",
                         [
                             ("http://dbpedia.org/resource/Nanyue_Huisi", "rdf"),
                             ("https://ephesusbreeze.com/es/tours", "jsonld")
                         ]
                         )
def test_visited_urls_and_to_be_visited_urls(input1, input2):
  crawler = Crawler()
  links = crawler.getLinks(input1, input2)
  visitedUrls = crawler.getVisitedUrls()
  toBeVisitedUrls = crawler.getToBeVisitedUrls()
  assert len(visitedUrls) == 1
  assert len(toBeVisitedUrls) > 1


@pytest.mark.parametrize("input1, input2",
                         [
                             ("http://dbpedia.org/resource/Nanyue_Huisi", "rdf"),
                             ("https://ephesusbreeze.com/es/tours", "jsonld")
                         ]
                         )
def test_returned_links_have_proper_domain(input1, input2):
  crawler = Crawler()
  links = crawler.getLinks(input1, input2)
  domain = urlparse(input1).netloc
  for link in links:
    assert urlparse(link).netloc == domain

@pytest.mark.parametrize("input1, input2",
                         [
                             ("http://dbpedia.org/resource/Nanyue_Huisi", "rdf"),
                             ("https://ephesusbreeze.com/es/tours", "jsonld")
                         ]
                         )
def test_should_not_visit_same_url(input1, input2):
  crawler = Crawler()
  links = crawler.getLinks(input1, input2)
  links2 = crawler.getLinks(input1, input2)
  assert links2 == None

@pytest.mark.parametrize("input1, input2, input3",
                         [
                             ("http://dbpedia.org/resource/Nanyue_Huisi", "rdf", "http://dbpedia.org/sparql/"),
                             ("https://ephesusbreeze.com/es/tours", "jsonld","https://ephesusbreeze.com/pt/passeios")
                         ]
                         )
def test_should_not_put_visited_urls_in_to_be_visited(input1, input2, input3):
  crawler = Crawler()
  crawler.getLinks(input1, input2)
  crawler.getLinks(input3,input2)
  toBeVisitedUrls = crawler.getToBeVisitedUrls()
  visitedUrls = crawler.getVisitedUrls()
  for url in toBeVisitedUrls:
    assert url != input1
    assert url != input2


@pytest.mark.parametrize("input1, input2",
                         [
                             ("http://dbpedia.org/resource/Nanyue_Huisi", "rdf")
                         ]
                         )
def test_parse_rdf_from_html_should_return_dictionary(input1, input2):
  crawler = Crawler()
  rdfList = crawler.parseRDF(input1, input2)
  print(rdfList)
  assert type(rdfList) == dict
  assert len(rdfList) > 1


